#!/usr/bin/python3
import sys
import os
import math

sizes = []
if len(sys.argv) == 2:
    n = int(sys.argv[1])
    assert(n >= 2)
    sizes.append(n)
else:
    sizes = list(range(2, 22))

aH = {3: '11 / (sqrt2 ^ 7)', 4: '-251 / (sqrt2 ^ 16)', 5: '23161 / (sqrt2 ^ 29)', 6: '133988401 / (sqrt2 ^ 54)', 7: '12412280691169 / (sqrt2 ^ 87)', 8: '75555863006653472909761 / (sqrt2 ^ 152)', 9: '-15034347071300495523371427226121933183 / (sqrt2 ^ 247)', 10: '-51408163609015342607074675483845466135738782582421858486334207 / (sqrt2 ^ 410)', 11: '-3020737074222195801570819662666796128450316512028163018409803468047724698381419832180463358217727 / (sqrt2 ^ 641)', 12: '209491273914069228938927703159615696187222341014075909563660555762272712788540723338447145178084239202207812130136290161243408801398046679755101305728001 / (sqrt2 ^ 1012)', 13: '-1151074135396334387919834795721180970251375555500447368443282849418992649499280004315846178433543785616073455184573876077344869113756963755769546129473427682216472562846573417285126112567795750348322239947350172187493433189788894185682943 / (sqrt2 ^ 1575)', 14: ' / (sqrt2 ^ 2414)', 15: ' / (sqrt2 ^ 3707)', 16: '- / (sqrt2 ^ 5644)', 17: ' / (sqrt2 ^ 8537)', 18: ' / (sqrt2 ^ 12882)', 19: ' / (sqrt2 ^ 19331)', 20: ' / (sqrt2 ^ 28964)', 21: '- / (sqrt2 ^ 43227)'}
aL = {3: '-1 / (sqrt2 ^ 7)', 4: '13 / (sqrt2 ^ 16)', 5: '-119 / (sqrt2 ^ 29)', 6: '-988079 / (sqrt2 ^ 54)', 7: '73054448161 / (sqrt2 ^ 87)', 8: '34433006489313897025 / (sqrt2 ^ 152)', 9: '-15629794375924256137689018495406207 / (sqrt2 ^ 247)', 10: '37317028493000746132194989664756306857260930471704364830465 / (sqrt2 ^ 410)', 11: '-118539524694868417372560014251163965732742125644187826278828466195957535600046592270101154303-118539524694868417372560014251163965732742125644187826278828466195957535600046592270101154303 / (sqrt2 ^ 641)', 12: '-24202565025804503446880919280495496962775723756474832273553510459075370332791703697528494514667628460606935348513162892614621202977427470434276838399 / (sqrt2 ^ 1012)', 13: '116727447598390764678665817233767345744563180333380080300857832533053441670776296647361588764296697594744011638618784159473730122050585400885348292680116598024969032335978981329308680256040955280628423431701080000649038858505284196353 / (sqrt2 ^ 1575)', 14: ' / (sqrt2 ^ 2414)', 15: '- / (sqrt2 ^ 3707)', 16: ' / (sqrt2 ^ 5644)', 17: '- / (sqrt2 ^ 8537)', 18: '- / (sqrt2 ^ 12882)', 19: ' / (sqrt2 ^ 19331)', 20: '- / (sqrt2 ^ 28964)', 21: ' / (sqrt2 ^ 43227)'}

for n in sizes:
    q = 2 * n - (n<=2)
    n_str = str(n).zfill(2)
    if not os.path.exists(n_str):
        os.makedirs(n_str)
    ###########################################################################
    with open(n_str + '/pre.hsl', 'w') as file:
        file.write('Extended Dirac\n')
        file.write("|" + '0' * q + ">\n")
    ###########################################################################
    with open(n_str + "/pre.lsta", "w") as file:
        file.write('Constants\n')
        file.write('c0 := 0\n')
        file.write('c1 := 1\n')
        file.write('Root States 0\n')
        file.write('Transitions\n')
        file.write(f"[{1},1]({2}, {1}) -> {0}\n")
        for level in range(2, q+1):
            file.write(f"[{level},1]({2*level - 1}, {2*level - 1}) -> {2*level - 3}\n")
            file.write(f"[{level},1]({2*level}, {2*level - 1}) -> {2*level - 2}\n")
        file.write(f"[c0,1] -> {2*q-1}\n")
        file.write(f"[c1,1] -> {2*q}\n")
    ###########################################################################
    with open(n_str + '/circuit.qasm', 'w') as file:
        file.write('OPENQASM 2.0;\n')
        file.write('include "qelib1.inc";\n')
        file.write(f'qreg qubits[{q}];\n')
        file.write(f'x qubits[{q-1}];\n\n')
        file.write(f'h qubits[0];\n')
        for i in range(1, q, 2):
            file.write(f'h qubits[{i}];\n')
        if n == 2:
            file.write(f'h qubits[2];\n')
        iterations = math.floor(math.pi / (4 * math.asin(1 / pow(2, n/2))))
        # format for int i in [1:iterations] { ... }
        file.write(f'for int i in [1:{iterations}] {{\n')
        for i in reversed(range(3, q-1, 4)):
            file.write(f'x qubits[{i}];\n')
        file.write(f'x qubits[0];\n')
        if n >= 3:
            temp = []
            for t in range(2, q, 2):
                file.write(f'ccx qubits[{t-2}], qubits[{t-1}], qubits[{t}];\n')
                temp.append((t-2, t-1, t))
            file.write(f'cx qubits[{q-2}], qubits[{q-1}];\n')
            for qubits in reversed(temp):
                file.write(f'ccx qubits[{qubits[0]}], qubits[{qubits[1]}], qubits[{qubits[2]}];\n')
        else:
            assert(n == 2)
            file.write('ccx qubits[0], qubits[1], qubits[2];\n')
        for i in reversed(range(3, q-1, 4)):
            file.write(f'x qubits[{i}];\n')
        file.write(f'x qubits[0];\n')
        file.write('h qubits[0];\n')
        for i in range(1, n):
            file.write(f'h qubits[{2*i-1}];\n')
        file.write('x qubits[0];\n')
        for i in range(1, n):
            file.write(f'x qubits[{2*i-1}];\n')
        if n >= 3:
            temp = []
            for t in range(2, q-2, 2):
                file.write(f'ccx qubits[{t-2}], qubits[{t-1}], qubits[{t}];\n')
                temp.append((t-2, t-1, t))
            file.write(f'cz qubits[{2*n-4}], qubits[{2*n-3}];\n')
            for qubits in reversed(temp):
                file.write(f'ccx qubits[{qubits[0]}], qubits[{qubits[1]}], qubits[{qubits[2]}];\n')
        else:
            assert(n == 2)
            file.write('cz qubits[0], qubits[1];\n')
        file.write('x qubits[0];\n')
        for i in range(1, n):
            file.write(f'x qubits[{2*i-1}];\n')
        file.write('h qubits[0];\n')
        for i in range(1, n):
            file.write(f'h qubits[{2*i-1}];\n')
        file.write('}\n')
        file.write(f'h qubits[{q-1}];\n')
    ###########################################################################
    if n <= 2: continue
    ###########################################################################
    with open(n_str + '/post.lsta', 'w') as file:
        file.write('Constants\n')
        file.write(f'aH := {aH[n]}\n')
        file.write(f'aL := {aL[n]}\n')
        file.write('c0 := 0\n')
        file.write('Root States 0\n')
        file.write('Transitions\n')
        file.write('[1,1](2, 1) -> 0\n')
        file.write('[2,1](3, 3) -> 1\n')
        file.write('[2,1](3, 4) -> 2\n')
        file.write('[3,1](6, 5) -> 3\n')
        file.write('[3,1](7, 5) -> 4\n')
        for i in range(4, 2*n-1, 2): # maximum 2n-2
            file.write(f'[{i  },1]({3*(i-4)+ 8}, {3*(i-4)+ 8}) -> {3*(i-4)+ 5}\n')
            file.write(f'[{i  },1]({3*(i-4)+ 9}, {3*(i-4)+ 9}) -> {3*(i-4)+ 6}\n')
            if (i // 2) % 2:
                file.write(f'[{i},1]({3*(i-4)+9}, {3*(i-4)+10}) -> {3*(i-4)+ 7}\n')
            else:
                file.write(f'[{i},1]({3*(i-4)+10}, {3*(i-4)+9}) -> {3*(i-4)+ 7}\n')
            file.write(f'[{i+1},1]({3*(i-4)+11}, {3*(i-4)+11}) -> {3*(i-4)+ 8}\n')
            file.write(f'[{i+1},1]({3*(i-4)+12}, {3*(i-4)+11}) -> {3*(i-4)+ 9}\n')
            file.write(f'[{i+1},1]({3*(i-4)+13}, {3*(i-4)+11}) -> {3*(i-4)+10}\n')
        file.write(f'[{2*n-1+1},1]({3*(2*n-1-4)+11}, {3*(2*n-1-4)+11}) -> {3*(2*n-1-4)+ 8}\n')
        file.write(f'[{2*n-1+1},1]({3*(2*n-1-4)+11}, {3*(2*n-1-4)+12}) -> {3*(2*n-1-4)+ 9}\n')
        file.write(f'[{2*n-1+1},1]({3*(2*n-1-4)+11}, {3*(2*n-1-4)+13}) -> {3*(2*n-1-4)+10}\n')
        file.write(f'[c0,1] -> {3*(2*n-1-4)+11}\n')
        file.write(f'[aL,1] -> {3*(2*n-1-4)+12}\n')
        file.write(f'[aH,1] -> {3*(2*n-1-4)+13}')
    ###########################################################################
    with open(n_str + '/post.hsl', 'w') as file:
        file.write('Constants\n')
        file.write(f'aH := {aH[n]}\n')
        file.write(f'aL := {aL[n]}\n')
        file.write('Variable Order\n')
        file.write(' '.join(map(str, [1] + list(range(2, 2*(n-1)+1, 2)) + list(range(3, 2*n, 2)) + [2*n])) + '\n')
        file.write('Extended Dirac\n')
        file.write(f"aH |" + '01'*(n//2)+'0'*(n%2) + "> + aL |*> # |" + '0'*(n-1) + "1>\n")
    ###########################################################################